import React from 'react';
import { Layout, Button} from 'antd';

import './MainPage.css';
import myIcon from '../images/logo.png';
import { Link} from "react-router-dom";
import HttpUtil from './HttpUtil';
import ApiUtil from './ApiUtil';
const { Header, Content } = Layout;

class MainPage extends React.Component{
    constructor(props) {
        super(props);
        this.state={
          inputValue:''

        }
        this.handleClickBtn = this.handleClickBtn.bind(this);
    }

    handleChange(e){
      this.setState({
        inputValue:e.target.value
      })
    }

    handleClickBtn(event) {

      const movieName = {movie:this.state.inputValue};
      var str = JSON.stringify(movieName);
      // document.write(str+",<br />")
      console.log(typeof movieName)
      this.props.history.push("/ReviewOverall");
      HttpUtil.post('/ReviewOverall', str);

    }
    render(){
        return (
            <div>
            <title>main</title>
            <link rel="stylesheet" href="MainPage.css" />
            <header>
              <figure>
                <img src={myIcon} alt="logo" />
              </figure>
            </header>
            <section>
              <input type="text" className="search_content" placeholder="Please input movie name"
              onChange={this.handleChange.bind(this)} defaultValue={this.state.inputValue}/>
              <Button style={{margin:"20px"}} type="primary" onClick={this.handleClickBtn}>Search</Button>
              <input type="radio" className="selector" name="choice" defaultValue="movies name" />movie
              <input type="radio" className="selector" name="choice" defaultValue="review" />review
            </section>  
            <footer>
              <p>
                TTDS CW3
              </p>
            </footer>
          </div>
        );
    }
}

export default MainPage;
