import './ReviewOverall.css';
// import myIcon from '../images/logo.png';

// const { Header, Content } = Layout;
// Movie Name:
// Average rating:
// Genere:
// Review number:
const reviewInfo = [
    {
        key: "1",
        movieName: "The Matrix",
        averageRating: "8",
        genere: "Sci-fi",
        reviewNumber: "800",
    },
    {
        key: "2",
        movieName: "The Matrix 2",
        averageRating: "9",
        genere: "Sci-fi",
        reviewNumber: "90",
    },
    {
        key: "3",
        movieName: "The Matrix 3",
        averageRating: "7",
        genere: "Sci-fi",
        reviewNumber: "47",
    },
    {
        key: "4",
        movieName: "The Matrix 4",
        averageRating: "0",
        genere: "Sci-fi",
        reviewNumber: "0",
    },
]
// function SingleReview(props) {
