import { Button, message } from 'antd';
import React from 'react';
import HttpUtil from './HttpUtil';
// import { Layout, Button} from 'antd';

import './ReviewOverall.css';
// import myIcon from '../images/logo.png';
// const reviewInfo = [
//     {
//         key: "1",
//         movieName: "The Matrix",
//         averageRating: "8",
//         genere: "Sci-fi",
//         reviewNumber: "800",
//     },
